mapper.select:
SELECT *
	FROM `member`
	WHERE name = #{name}
	AND email = #{email}

mapper.update:
UPDATE `member`
		SET updateDate = NOW(),
			nickname = #{nickname},
			cellphoneNum = #{cellphoneNum},
			email = #{email}
		WHERE id = #{loginedMemberId}

UPDATE `member`
		SET updateDate = NOW(),
			loginPw = #{loginPw}
		WHERE id = #{loginedMemberId}
